import React, { useContext, useEffect, useState } from 'react'
import Footer from '../myPortfolio/Footer'
import Navbar from '../myPortfolio/Navbar'
import { dataContext } from './context/DataContext';




function Blog() {
    const context = useContext(dataContext);
    const [data, setData] = useState([])
    useEffect(() => { context.blogListesi().then((res) => {
        setData(res.data); }, [])
   
    })
    return (
        <>
            <Navbar />
            <Footer />
            <div className='container mt-4'>
                {
                    data.map((item, index) => {
                        return (
                            <div className="card mb-3 postCard" key={index}>
                                <div className="row g-0">
                                    <div className="col-md-4">
                                        <img src={item.imageUrl} className="img-fluid rounded-start" alt="..." />
                                    </div>
                                    <div className="col-md-8">
                                        <div className="card-body">
                                            <h5 className="card-title fw-bold">{item.title}</h5>
                                            <p className="card-text">{item.description}</p>
                                            <p className="card-text"><small className="text-muted"> Creation Date : {item.date}</small></p>
                                            <button className="btn btn-primary float-end mt-5" data-bs-target="#exampleModal" data-bs-toggle="modal">Go somewhere</button>

                                        </div>
                                    </div>
                                </div>
                            </div>
                        )
                    })
                }


            </div>
            <div className="modal-dialog modal-fullscreen-sm-down" id="exampleModal"  aria-labelledby="exampleModal" aria-hidden="true">
                Modal
            </div>
        </>
    )
}

export default Blog
