import React, { useContext, useEffect, useState } from 'react'
import Footer from '../myPortfolio/Footer'
import Navbar from '../myPortfolio/Navbar'
import { dataContext } from './context/DataContext';




function Blog() {
    const context = useContext(dataContext);
    const [data, setData] = useState([])
    useEffect(() => { 
        context.blogListesi().then((res) => {
            setData(res.data); 
        }); 
    }, []);
    return (
        <>
            <Navbar />           
            <div className='container' id='blogPostContainer'>
                {
                    data.map((item, index) => {
                        if(item.active==true){
                            return (
                                <div className="card mb-3 postCard" key={index}>
                                    <div className="row g-0">
                                        <div className="col-md-4">
                                            <img src={item.imageUrl} className="img-fluid rounded-start" alt="..." />
                                        </div>
                                        <div className="col-md-8">
                                            <div className="card-body">
                                                <h5 className="card-title fw-bold">{item.title}</h5>
                                                <p className="card-text">{item.description}</p>
                                                <p className="card-text"><small className="text-muted"> Creation Date : {item.date}</small></p>
                                                <button className="btn btn-primary float-end mt-5" data-bs-target={`#${item.id}`} data-bs-toggle="modal">Go somewhere</button>
    
                                            </div>
                                        </div>
                                    </div>
                                </div>
                            )
                        }
                       
                    })
                }
            </div>
           {
            data.map((item,index)=>{
                return(
                    <div className="modal fade" id={item.id} tabIndex="-1" aria-labelledby="exampleModalLabel" aria-hidden="true" key={index}>
                    <div className="modal-dialog  modal-fullscreen myModal">
                        <div className="modal-content">
                            <div className="modal-header fw-bold">
                                <h1 className="modal-title fs-5 w-100 fw-bold text-uppercase" id="exampleModalLabel">{item.title}</h1>
                                <button type="button" className="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
                            </div>
                            <div className="modal-body">
                            <img src={item.imageUrl} alt=".." className="img-fluid d-block mx-auto" id='modalImage' />
                            <p className="my-4 col-lg-9 mx-auto">{item.content}</p>
                            
                            </div>
                            <div className="modal-footer">
                                <p className="my-4 col-lg-9 mx-auto "><small className="text-muted"> Creation Date : {item.date}</small></p>
                                <button type="button" className="btn btn-secondary" data-bs-dismiss="modal">Close</button>
                            </div>
                        </div>
                    </div>
                </div>
                )
            })
           }
           <Footer />
        </>
    )
}

export default Blog
