import './App.css';
import './Responsive.css';
import Navbar from './components/Navbar';
import Footer from './components/Footer';
import { Route, Router, Routes, ScrollRestoration, RestoredScroll } from 'react-router-dom';
import Home from './components/Home';
import Blog from './components/blog/Blog';


function App() {
  return (
    <div className="App">
     <Navbar/>
     <Routes>
      <Router>
      <ScrollRestoration>
      <RestoredScroll id="#about">
      <Route  path='' element={<Home/>}/>
      </RestoredScroll>
      <Route  path='bloglist' element={<Blog/>}/>
      </ScrollRestoration>
      </Router>
     </Routes>
     <Footer/>
    </div>
  );
}

export default App;
