import './App.css';
import { Route, Routes, useLocation } from 'react-router-dom';
import Home from './components/myPortfolio/Home';
import Blog from './components/blog/Blog';
import { useEffect } from 'react';
import AdminPanel from './components/blog/adminpanel/AdminPanel';
import BlogList from './components/blog/adminpanel/pages/BlogList'
import BlogAdd from './components/blog/adminpanel/pages/BlogAdd'
import Command from './components/blog/adminpanel/pages/Command'
import DataContext from './components/blog/context/DataContext';


function App() {
  const { pathname, hash, key } = useLocation();
  useEffect(() => {
    // if not a hash link, scroll to top
    if (hash === '') {
      window.scrollTo(0, 0);
    }
    // else scroll to id
    else {
      setTimeout(() => {
        const id = hash.replace('#', '');
        const element = document.getElementById(id);
        if (element) {
          element.scrollIntoView();
        }
      }, 0);
    }
  }, [pathname, hash, key]); // do this on route change

  return (
    <>
      <DataContext>
        <Routes>
          <Route path='/' element={<Home />} />
          <Route path='/postlist' element={<DataContext><Blog /></DataContext>} />
          <Nav />
          <Aside />
          <Route path='/admin' element={<AdminPanel />} />
          <Route path='/admin/bloglist' element={<DataContext><BlogList /></DataContext>} />
          <Route path='/admin/blogadd' element={<DataContext><BlogAdd /></DataContext>} />
          <Route path='/admin/command' element={<DataContext><Command /></DataContext>} />
        </Routes>
      </DataContext>
    </>
  );
}

export default App;
