import React, { useContext, useState } from 'react'
import Footer from '../myPortfolio/Footer'
import Navbar from '../myPortfolio/Navbar'
import { dataContext } from './context/DataContext';




function Blog() {
    const context = useContext(dataContext);
    const [data, setData] = useState([])
    context.blogListesi().then((res) => {
        setData(res.data);
    })
    return (
        <>
            <Navbar />
            <Footer />
            <div className='container mt-4 fw-bold'>
                {
                    data.map((item, index) => {
                        return(
                        <div className="card mb-3 postCard" key={index}>
                            <div className="row g-0">
                                <div className="col-md-4">
                                    <img src={item.imageUrl} className="img-fluid rounded-start" alt="..." />
                                </div>
                                <div className="col-md-8">
                                    <div className="card-body">
                                        <h5 className="card-title">{item.title}</h5>
                                        <p className="card-text">{item.description}</p>
                                        <p className="card-text float-end mt-3"><small className="text-muted">{item.date}</small></p>
                                        <a href="#" className="btn btn-primary ">Go somewhere</a>
                                    </div>
                                </div>
                            </div>
                        </div>
                        )
                    })
                }


            </div>
        </>
    )
}

export default Blog
