import React, { useContext } from 'react';
import Aside from '../Aside';
import Nav from '../Nav';
import PageHeader from './PageHeader';
import DataContext from '../../context/DataContext';

function BlogAdd() {
  const { dispatch } = useContext(DataContext);
  return (
    <div>
      <Nav />
      <Aside />
      <div className='content-wrapper'>
        <PageHeader title={"Blog Ekle"} />
        <div className='content'>
          <section className="content">
            <div className="card">
              <div className="card-header">
                <h3 className="card-title">Blog Bilgileri</h3>
              </div>
              <div className="card-body">
              <div className="form-group">
                    <label htmlFor="baslik">Başlık</label>
                    <input type="text" className="form-control" name='baslik' id="baslik" placeholder="Blog Başlığı"/>
                  </div>
                  <div className="form-group">
                    <label htmlFor="aciklama">Açıklama</label>
                    <input type="text" className="form-control" name='aciklama' id="aciklama" placeholder="Blog Açıklaması"/>
                  </div>
                  <div className="form-group">
                  <div className="form-check">
                          <input className="form-check-input" type="checkbox" id="aktiflik"/>
                          <label className="form-check-label">Aktiflik Durumu</label>
                        </div>
                  </div>
                  <div className="form-group">
                    <label htmlFor="icerik">İçerik</label>
                    <textarea rows="3" className="form-control" name='icerik' id="icerik"></textarea>
                  </div>

              </div>

              <div className="card-footer">
                <button onClick={()=>{
                  dispatch(
                    {
                      type:"addblog",
                      data:{
                        title:document.getElementById("baslik").value,
                        description:document.getElementById("aciklama").value,
                        content:document.getElementById("icerik").value,
                        status:document.getElementById("aktiflik").value,
                      }
                    }
                  );
                }} className='btn btn-primary float-end' type='button'>Kaydet</button>
              </div>

            </div>

          </section>
        </div>
      </div>
    </div>
  );
}

export default BlogAdd;
